import{_ as l,c as i,o as e,a}from"./app.e13fd61d.js";const b=JSON.parse('{"title":"生物学","description":"","frontmatter":{},"headers":[{"level":2,"title":"必修一：分子与细胞","slug":"必修一-分子与细胞","link":"#必修一-分子与细胞","children":[{"level":3,"title":"第一章：细胞的分子组成","slug":"第一章-细胞的分子组成","link":"#第一章-细胞的分子组成","children":[]},{"level":3,"title":"第二章：细胞的结构和生命活动","slug":"第二章-细胞的结构和生命活动","link":"#第二章-细胞的结构和生命活动","children":[]},{"level":3,"title":"第三章：细胞中能量的转换和利用","slug":"第三章-细胞中能量的转换和利用","link":"#第三章-细胞中能量的转换和利用","children":[]},{"level":3,"title":"第四章：细胞增殖、分化、衰老和死亡","slug":"第四章-细胞增殖、分化、衰老和死亡","link":"#第四章-细胞增殖、分化、衰老和死亡","children":[]}]},{"level":2,"title":"必修二：遗传与进化","slug":"必修二-遗传与进化","link":"#必修二-遗传与进化","children":[{"level":3,"title":"第一章：遗传的细胞基础","slug":"第一章-遗传的细胞基础","link":"#第一章-遗传的细胞基础","children":[]},{"level":3,"title":"第二章：遗传的分子基础","slug":"第二章-遗传的分子基础","link":"#第二章-遗传的分子基础","children":[]},{"level":3,"title":"第三章：生物的变异","slug":"第三章-生物的变异","link":"#第三章-生物的变异","children":[]},{"level":3,"title":"第四章：生物的进化","slug":"第四章-生物的进化","link":"#第四章-生物的进化","children":[]}]},{"level":2,"title":"选必一：稳态与调节","slug":"选必一-稳态与调节","link":"#选必一-稳态与调节","children":[{"level":3,"title":"第一章：人体稳态维持的生理基础","slug":"第一章-人体稳态维持的生理基础","link":"#第一章-人体稳态维持的生理基础","children":[]},{"level":3,"title":"第二章：人体内环境与稳态","slug":"第二章-人体内环境与稳态","link":"#第二章-人体内环境与稳态","children":[]},{"level":3,"title":"第三章：人的免疫调节与稳态","slug":"第三章-人的免疫调节与稳态","link":"#第三章-人的免疫调节与稳态","children":[]},{"level":3,"title":"第四章：植物生命活动的调节","slug":"第四章-植物生命活动的调节","link":"#第四章-植物生命活动的调节","children":[]}]},{"level":2,"title":"选必二：生物与环境","slug":"选必二-生物与环境","link":"#选必二-生物与环境","children":[{"level":3,"title":"第一章：种群","slug":"第一章-种群","link":"#第一章-种群","children":[]},{"level":3,"title":"第二章：群落","slug":"第二章-群落","link":"#第二章-群落","children":[]},{"level":3,"title":"第三章：生态系统","slug":"第三章-生态系统","link":"#第三章-生态系统","children":[]},{"level":3,"title":"第四章：生态环境的保护","slug":"第四章-生态环境的保护","link":"#第四章-生态环境的保护","children":[]}]},{"level":2,"title":"选必三：生物技术与工程","slug":"选必三-生物技术与工程","link":"#选必三-生物技术与工程","children":[{"level":3,"title":"第一章：发酵工程","slug":"第一章-发酵工程","link":"#第一章-发酵工程","children":[]},{"level":3,"title":"第二章：细胞工程","slug":"第二章-细胞工程","link":"#第二章-细胞工程","children":[]},{"level":3,"title":"第三章：基因工程","slug":"第三章-基因工程","link":"#第三章-基因工程","children":[]},{"level":3,"title":"第四章：生物技术安全与伦理问题","slug":"第四章-生物技术安全与伦理问题","link":"#第四章-生物技术安全与伦理问题","children":[]}]}],"relativePath":"biology/index.md","lastUpdated":1672322705000}'),r={name:"biology/index.md"},h=a('<h1 id="生物学" tabindex="-1">生物学 <a class="header-anchor" href="#生物学" aria-hidden="true">#</a></h1><h2 id="必修一-分子与细胞" tabindex="-1">必修一：分子与细胞 <a class="header-anchor" href="#必修一-分子与细胞" aria-hidden="true">#</a></h2><blockquote><p>兴趣是最好的老师。</p></blockquote><h3 id="第一章-细胞的分子组成" tabindex="-1">第一章：细胞的分子组成 <a class="header-anchor" href="#第一章-细胞的分子组成" aria-hidden="true">#</a></h3><h4 id="第一节-细胞中的元素和无机化合物" tabindex="-1">第一节：细胞中的元素和无机化合物 <a class="header-anchor" href="#第一节-细胞中的元素和无机化合物" aria-hidden="true">#</a></h4><ol><li><p>构成细胞的元素</p><ul><li><strong>大量元素</strong>：元素在生物体内含量较多，如 C、H、O、N、P、S、K、Ca、Mg、Na、Cl 等；</li><li><strong>微量元素</strong>：元素在生物体内含量很少，如 Fe、Mn、Zn、Cu、B、Mo、Co、Se 等。</li></ul></li><li><p>水和无机盐是细胞中的无机化合物</p><ul><li><strong>自由水</strong>以游离的形式存在，参与细胞中各种代谢活动。</li><li><strong>结合水</strong>是被细胞内其他物质（如蛋白质）束缚而不易自由流动的水，是组成细胞的重要成分。</li><li>细胞中的<strong>无机盐</strong>含量虽小，但具有重要的生理功能，与生命活动关系密切。</li></ul></li></ol><h4 id="第二节-细胞中的糖类和脂质" tabindex="-1">第二节：细胞中的糖类和脂质 <a class="header-anchor" href="#第二节-细胞中的糖类和脂质" aria-hidden="true">#</a></h4><ol><li><p>碳链是形成生物大分子的骨架</p><ul><li>多糖、蛋白质和核酸是生物大分子，他们都是由许多基本组成单位（单体）连接而成的。构成这些单体的碳原子之间是通过单键或双键相连接的，形成了不同长度的分支状、链状或环状结构，这些结构被称为有机化合物的<strong>碳骨架</strong>。</li></ul></li><li><p>糖类是细胞的重要结构成分和主要能源物质</p><ul><li><strong>糖类</strong>即使细胞的重要结构成分，又是生物体维持生命活动的主要能源物质。</li></ul></li><li><p>脂质在维持细胞结构和功能中的作用</p><ul><li><strong>脂肪</strong>是细胞内重要的储能物质。</li></ul></li></ol><h4 id="第三节-细胞中的蛋白质和核酸" tabindex="-1">第三节：细胞中的蛋白质和核酸 <a class="header-anchor" href="#第三节-细胞中的蛋白质和核酸" aria-hidden="true">#</a></h4><ol><li><p>细胞的功能主要由蛋白质完成</p><ul><li>在每个<strong>氨基酸分子</strong>中，至少都含有一个氨基（—NH<sub>2</sub>）和一个羧基（—COOH），并且都有一个氨基和一个羧基与同一个碳原子相连接，这个碳原子还分别与一个氢原子、一个侧链基团（R）相连接。</li><li><strong>蛋白质</strong>参与组成细胞和生物体的各种结构，并具有特定的生理功能。</li></ul></li><li><p>核酸储存与传递遗传信息</p><ul><li><strong>核酸</strong>是生物体中储存与传递遗传信息的生物大分子，在生物体的遗传、变异和蛋白质的生物合成中具有十分重要的作用。</li></ul></li></ol><h3 id="第二章-细胞的结构和生命活动" tabindex="-1">第二章：细胞的结构和生命活动 <a class="header-anchor" href="#第二章-细胞的结构和生命活动" aria-hidden="true">#</a></h3><h4 id="第一节-细胞学说——现代生物学的-基石" tabindex="-1">第一节：细胞学说——现代生物学的“基石” <a class="header-anchor" href="#第一节-细胞学说——现代生物学的-基石" aria-hidden="true">#</a></h4><ol><li><p>细胞学说的建立与发展</p><ul><li><strong>细胞学说</strong>：一切动物和植物都是由细胞组成的，细胞是一切动物和植物体的基本单位。</li></ul></li><li><p>生物学研究的重要工具——显微镜</p></li><li><p>细胞的大小和形态</p><ul><li>除了病毒外，所有生物体都是由<strong>细胞</strong>构成的。</li></ul></li></ol><h4 id="第二节-细胞——生命活动的基本单位" tabindex="-1">第二节：细胞——生命活动的基本单位 <a class="header-anchor" href="#第二节-细胞——生命活动的基本单位" aria-hidden="true">#</a></h4><ol><li><p>细胞质膜将细胞与外界环境分开</p><ul><li><strong>细胞质膜</strong>主要由脂质和蛋白质组成。</li></ul></li><li><p>结构与功能独特的细胞器</p><ul><li><strong>细胞质基质</strong>是生命活动的重要场所，为生命活动提供了一定的环境条件以及所需要的物质和能量。</li></ul></li><li><p>遗传信息主要储存在细胞核中</p><ul><li><strong>染色质和染色体</strong>是细胞中同一种物质在不同时期的两种不同形态。</li></ul></li><li><p>细胞各部分结构分工合作</p></li><li><p>原核细胞和真核细胞的主要区别</p></li></ol><h4 id="第三节-物质进出细胞的运输方式" tabindex="-1">第三节：物质进出细胞的运输方式 <a class="header-anchor" href="#第三节-物质进出细胞的运输方式" aria-hidden="true">#</a></h4><ol><li><p>细胞质膜具有选择透过性</p></li><li><p>被动运输不需要细胞供能</p></li><li><p>主动运输需要细胞供能</p><ul><li>离子或小分子物质逆浓度梯度进出细胞，不仅需要细胞膜上载体蛋白的参与，还需要细胞代谢供能的物质运输方式，称为<strong>主动运输</strong>。</li></ul></li><li><p>细胞的胞吞和胞吐</p></li></ol><h3 id="第三章-细胞中能量的转换和利用" tabindex="-1">第三章：细胞中能量的转换和利用 <a class="header-anchor" href="#第三章-细胞中能量的转换和利用" aria-hidden="true">#</a></h3><h4 id="第一节-生命活动需要酶和能源物质" tabindex="-1">第一节：生命活动需要酶和能源物质 <a class="header-anchor" href="#第一节-生命活动需要酶和能源物质" aria-hidden="true">#</a></h4><ol><li><p>生物催化剂——酶</p><ul><li>能催化生化反应的<strong>酶</strong>绝大多数是蛋白质。</li><li>由酶催化的化学反应称为<strong>酶促反应</strong>。</li><li>酶催化作用的实质是降低化学反应的<strong>活化能</strong>。</li><li><strong>酶活性</strong>不仅与反应体系中的酶浓度和底物浓度等有密切关系，还受多种环境因素如温度、pH 等的影响。</li></ul></li><li><p>ATP 是驱动细胞生命活动的直接能源物质</p></li></ol><h4 id="第二节-光合作用——光能的捕获和转换" tabindex="-1">第二节：光合作用——光能的捕获和转换 <a class="header-anchor" href="#第二节-光合作用——光能的捕获和转换" aria-hidden="true">#</a></h4><ol><li><p>解开光合作用之谜</p></li><li><p>叶绿体与光能的捕获</p></li><li><p>绿色植物光合作用的过程</p><ul><li>叶绿体是绿色植物进行光合作用的场所。<strong>光合作用</strong>是绿色植物细胞中的叶绿体从太阳光中捕获能量，并将这些能量在 CO<sub>2</sub>和 H<sub>2</sub>O 转变为糖与 O<sub>2</sub>的过程中，转换并储存为糖分子中化学能的过程。</li></ul></li></ol><h4 id="第三节-细胞呼吸——能量的转化和利用" tabindex="-1">第三节：细胞呼吸——能量的转化和利用 <a class="header-anchor" href="#第三节-细胞呼吸——能量的转化和利用" aria-hidden="true">#</a></h4><ol><li><p>细胞有氧呼吸是大多数生物获取能量的主要途径</p><ul><li><strong>细胞呼吸</strong>主要是指糖类、脂质和蛋白质等有机物在活细胞内氧化分解为 CO<sub>2</sub>或其他物质，同时释放出能量并生成 ATP 的过程。</li><li><strong>有氧呼吸</strong>是指在氧的参与下，细胞内的有机物彻底氧化分裂产生二氧化碳和水，同时释放能量，生成大量 ATP 的过程。细胞有氧呼吸主要发生在线粒体内。</li></ul></li><li><p>细胞无氧呼吸也为生命活动提供能量</p><ul><li>在无氧或缺氧的条件下，细胞通过酶的催化作用，把葡萄糖等有机物分解为乙醇（C<sub>2</sub>H<sub>5</sub>OH）和 CO<sub>2</sub>，或分解为乳酸（C<sub>3</sub>H<sub>6</sub>O<sub>3</sub>）等物质，同时释放较少能量的过程，被称为<strong>无氧呼吸</strong>。细胞无氧呼吸过程发生在细胞质基质中。</li></ul></li><li><p>细胞呼吸产生能量的利用</p></li></ol><h4 id="第四节-影响光合作用和细胞呼吸的环境因素" tabindex="-1">第四节：影响光合作用和细胞呼吸的环境因素 <a class="header-anchor" href="#第四节-影响光合作用和细胞呼吸的环境因素" aria-hidden="true">#</a></h4><ol><li><p>影响光合作用的环境因素</p></li><li><p>影响细胞呼吸的环境因素</p></li><li><p>光合作用和细胞呼吸原理的应用</p></li></ol><h3 id="第四章-细胞增殖、分化、衰老和死亡" tabindex="-1">第四章：细胞增殖、分化、衰老和死亡 <a class="header-anchor" href="#第四章-细胞增殖、分化、衰老和死亡" aria-hidden="true">#</a></h3><h4 id="第一节-细胞增殖" tabindex="-1">第一节：细胞增殖 <a class="header-anchor" href="#第一节-细胞增殖" aria-hidden="true">#</a></h4><ol><li><p>生物体的生长主要是细胞增殖的结果</p></li><li><p>细胞增殖和有丝分裂</p><ul><li>连续分裂的细胞，从一次细胞分裂结束开始，经过物质准备，直到下一次细胞分裂结束为止的过程，称为一个<strong>细胞周期</strong>。</li><li>在分裂间期，染色体复制（主要是 DNA 复制）一次，在<strong>有丝分裂</strong>中，细胞分裂一次，分裂结果是染色体平均分配到两个子代细胞中，这保证了子代细胞与亲代细胞中遗传信息的一致性。</li></ul></li><li><p>减数分裂和无丝分裂</p><ul><li><strong>减数分裂</strong>是一种特殊的有丝分裂，是产生有性生殖细胞的分裂方式。</li><li><strong>无丝分裂</strong>过程相对简单，在分裂过程中，细胞内没有染色体和纺锤体等结构的出现。</li></ul></li></ol><h4 id="第二节-细胞分化、衰老和死亡" tabindex="-1">第二节：细胞分化、衰老和死亡 <a class="header-anchor" href="#第二节-细胞分化、衰老和死亡" aria-hidden="true">#</a></h4><ol><li><p>细胞分化和细胞全能性</p><ul><li>在个体发育中，由一个或一种细胞经细胞分裂产生的子代细胞，在形态结构和生理功能上逐渐发生稳定性差异的过程，称为<strong>细胞分化</strong>。</li><li>细胞的<strong>全能性</strong>是指细胞分裂和分化后，仍然具有产生完整有机体或分化成其他各种细胞的潜能和特性。</li></ul></li><li><p>细胞衰老和细胞死亡</p><ul><li><strong>细胞凋亡</strong>是由基因所决定的细胞自动结束生命的过程。</li></ul></li></ol><h2 id="必修二-遗传与进化" tabindex="-1">必修二：遗传与进化 <a class="header-anchor" href="#必修二-遗传与进化" aria-hidden="true">#</a></h2><blockquote><p>重视观察与思考。</p></blockquote><h3 id="第一章-遗传的细胞基础" tabindex="-1">第一章：遗传的细胞基础 <a class="header-anchor" href="#第一章-遗传的细胞基础" aria-hidden="true">#</a></h3><h3 id="第二章-遗传的分子基础" tabindex="-1">第二章：遗传的分子基础 <a class="header-anchor" href="#第二章-遗传的分子基础" aria-hidden="true">#</a></h3><h3 id="第三章-生物的变异" tabindex="-1">第三章：生物的变异 <a class="header-anchor" href="#第三章-生物的变异" aria-hidden="true">#</a></h3><h3 id="第四章-生物的进化" tabindex="-1">第四章：生物的进化 <a class="header-anchor" href="#第四章-生物的进化" aria-hidden="true">#</a></h3><h2 id="选必一-稳态与调节" tabindex="-1">选必一：稳态与调节 <a class="header-anchor" href="#选必一-稳态与调节" aria-hidden="true">#</a></h2><blockquote><p>要像科学家一样关注关爱生命的问题。</p></blockquote><h3 id="第一章-人体稳态维持的生理基础" tabindex="-1">第一章：人体稳态维持的生理基础 <a class="header-anchor" href="#第一章-人体稳态维持的生理基础" aria-hidden="true">#</a></h3><h3 id="第二章-人体内环境与稳态" tabindex="-1">第二章：人体内环境与稳态 <a class="header-anchor" href="#第二章-人体内环境与稳态" aria-hidden="true">#</a></h3><h3 id="第三章-人的免疫调节与稳态" tabindex="-1">第三章：人的免疫调节与稳态 <a class="header-anchor" href="#第三章-人的免疫调节与稳态" aria-hidden="true">#</a></h3><h3 id="第四章-植物生命活动的调节" tabindex="-1">第四章：植物生命活动的调节 <a class="header-anchor" href="#第四章-植物生命活动的调节" aria-hidden="true">#</a></h3><h2 id="选必二-生物与环境" tabindex="-1">选必二：生物与环境 <a class="header-anchor" href="#选必二-生物与环境" aria-hidden="true">#</a></h2><blockquote><p>重视学科间的横向联系。</p></blockquote><h3 id="第一章-种群" tabindex="-1">第一章：种群 <a class="header-anchor" href="#第一章-种群" aria-hidden="true">#</a></h3><h3 id="第二章-群落" tabindex="-1">第二章：群落 <a class="header-anchor" href="#第二章-群落" aria-hidden="true">#</a></h3><h3 id="第三章-生态系统" tabindex="-1">第三章：生态系统 <a class="header-anchor" href="#第三章-生态系统" aria-hidden="true">#</a></h3><h3 id="第四章-生态环境的保护" tabindex="-1">第四章：生态环境的保护 <a class="header-anchor" href="#第四章-生态环境的保护" aria-hidden="true">#</a></h3><h2 id="选必三-生物技术与工程" tabindex="-1">选必三：生物技术与工程 <a class="header-anchor" href="#选必三-生物技术与工程" aria-hidden="true">#</a></h2><blockquote><p>要积极参与实验活动。</p></blockquote><h3 id="第一章-发酵工程" tabindex="-1">第一章：发酵工程 <a class="header-anchor" href="#第一章-发酵工程" aria-hidden="true">#</a></h3><h3 id="第二章-细胞工程" tabindex="-1">第二章：细胞工程 <a class="header-anchor" href="#第二章-细胞工程" aria-hidden="true">#</a></h3><h3 id="第三章-基因工程" tabindex="-1">第三章：基因工程 <a class="header-anchor" href="#第三章-基因工程" aria-hidden="true">#</a></h3><h3 id="第四章-生物技术安全与伦理问题" tabindex="-1">第四章：生物技术安全与伦理问题 <a class="header-anchor" href="#第四章-生物技术安全与伦理问题" aria-hidden="true">#</a></h3>',55),n=[h];function t(d,s,o,u,c,g){return e(),i("div",null,n)}const f=l(r,[["render",t]]);export{b as __pageData,f as default};
