import{_ as d,c as t,o as l,a as e}from"./app.9c9d8a6a.js";const f=JSON.parse('{"title":"写作指南","description":"","frontmatter":{"title":"写作指南"},"headers":[{"level":2,"title":"常见中英文标点符号对照表","slug":"常见中英文标点符号对照表","link":"#常见中英文标点符号对照表","children":[]},{"level":2,"title":"写作规则","slug":"写作规则","link":"#写作规则","children":[{"level":3,"title":"空行","slug":"空行","link":"#空行","children":[]},{"level":3,"title":"空格","slug":"空格","link":"#空格","children":[]},{"level":3,"title":"标点符号","slug":"标点符号","link":"#标点符号","children":[]},{"level":3,"title":"全角和半角","slug":"全角和半角","link":"#全角和半角","children":[]},{"level":3,"title":"名词","slug":"名词","link":"#名词","children":[]}]}],"relativePath":"others/writing-guidelines/index.md","lastUpdated":1666017254000}'),i={name:"others/writing-guidelines/index.md"},o=e('<h1 id="写作指南" tabindex="-1">写作指南 <a class="header-anchor" href="#写作指南" aria-hidden="true">#</a></h1><h2 id="常见中英文标点符号对照表" tabindex="-1">常见中英文标点符号对照表 <a class="header-anchor" href="#常见中英文标点符号对照表" aria-hidden="true">#</a></h2><table><thead><tr><th>/</th><th>中文标点</th><th>英文标点</th></tr></thead><tbody><tr><td>逗号</td><td>，</td><td>,</td></tr><tr><td>句号</td><td>。</td><td>.</td></tr><tr><td>问号</td><td>？</td><td>?</td></tr><tr><td>感叹号</td><td>！</td><td>!</td></tr><tr><td>冒号</td><td>：</td><td>:</td></tr><tr><td>分号</td><td>；</td><td>;</td></tr><tr><td>单引号</td><td>‘’</td><td>&#39;&#39;</td></tr><tr><td>双引号</td><td>“”</td><td>&quot;&quot;</td></tr><tr><td>省略号</td><td>……</td><td>...</td></tr><tr><td>破折号</td><td>——</td><td>-</td></tr><tr><td>圆括号</td><td>（）</td><td>()</td></tr><tr><td>方括号</td><td>【】</td><td>[]</td></tr></tbody></table><h2 id="写作规则" tabindex="-1">写作规则 <a class="header-anchor" href="#写作规则" aria-hidden="true">#</a></h2><h3 id="空行" tabindex="-1">空行 <a class="header-anchor" href="#空行" aria-hidden="true">#</a></h3><ul><li>不要有多余的空行</li><li>文件末尾空一行</li><li>标题前后各空一行</li></ul><h3 id="空格" tabindex="-1">空格 <a class="header-anchor" href="#空格" aria-hidden="true">#</a></h3><ul><li><p>中英文之间空格</p><ul><li>正确：<code>Microsoft 推出的 Edge 与 Google Chrome 相比体验如何？</code></li><li>错误：<code>Microsoft推出的Edge与Google Chrome相比体验如何？</code></li></ul></li><li><p>中文与数字之间空格</p><ul><li>正确：<code>今天是 2022 年 10 月 17 日。</code></li><li>错误：<code>今天是2022年10月17日。</code></li></ul></li><li><p>数字与单位之间空格</p><ul><li>正确：<code>100 Gbps的宽带，下载速度约为 12.5 MB/s</code></li><li>错误：<code>100Gbps的宽带，下载速度约为 12.5MB/s</code></li></ul></li><li><p>度 (°) 或百分比 (%) 与数字之间不空格</p><ul><li>正确：<code>60° 的三角形的面积是原来的 50%。</code></li><li>错误：<code>60 ° 的三角形的面积是原来的 50 %。</code></li></ul></li><li><p>全角标点与其他字符之间不空格</p><ul><li>正确：<code>终于下班了，回家！</code></li><li>错误：<code>终于下班了 ， 回家！</code></li><li>正确：<code>前端技术有 HTML、CSS 和 JavaScript 等。</code></li><li>错误：<code>前端技术有 HTML 、 CSS 和 JavaScript 等。</code></li></ul></li></ul><h3 id="标点符号" tabindex="-1">标点符号 <a class="header-anchor" href="#标点符号" aria-hidden="true">#</a></h3><ul><li><p>不重复使用标点符号</p><ul><li>正确：<code>你在说什么？！</code></li><li>错误：<code>你在说什么？？</code></li></ul></li></ul><h3 id="全角和半角" tabindex="-1">全角和半角 <a class="header-anchor" href="#全角和半角" aria-hidden="true">#</a></h3><ul><li><p>使用全角中文标点</p><ul><li>正确：<code>心之所向，素履以往。</code></li><li>错误：<code>心之所向, 素履以往.</code></li></ul></li><li><p>数字使用半角字符</p><ul><li>正确：<code>这个东西卖 100 元。</code></li><li>错误：<code>这个东西卖１００元。</code></li></ul></li><li><p>完整的英文整句、特殊名词，其内容使用半角标点</p><ul><li>正确：<code>经常看到“Hello, world!”这些句子。</code></li><li>错误：<code>经常看到“Hello，world！”这些句子。</code></li></ul></li></ul><h3 id="名词" tabindex="-1">名词 <a class="header-anchor" href="#名词" aria-hidden="true">#</a></h3><ul><li><p>专有名词使用正确的大小写</p><ul><li>正确：<code>知名的有 GitHub、Microsoft Corporation、Google 和 Facebook, Inc 等。</code></li><li>错误：<code>知名的有 github、microsoft corporation、google 和 facebook, inc 等。</code></li></ul></li><li><p>使用地道的缩写</p><ul><li>正确：<code>前端需要熟悉 HTML5、CSS3 和 JavaScript 等。</code></li><li>错误：<code>前端需要熟悉 h5、css3 和 js 等。</code></li></ul></li></ul>',14),r=[o];function a(c,h,n,s,u,p){return l(),t("div",null,r)}const g=d(i,[["render",a]]);export{f as __pageData,g as default};
